import { useState, useEffect } from 'react';
import '../../css/student.css'; // Make sure this path is correct

const StudentDashboard = () => {
  const [isNavVisible, setIsNavVisible] = useState(true);
  const [activeLink, setActiveLink] = useState('Dashboard');

  const handleNavToggle = () => {
    setIsNavVisible(!isNavVisible);
  };

  const handleLinkClick = (name) => {
    setActiveLink(name);
  };

  useEffect(() => {
    // You can add code here to handle any side effects if needed
    // For example, initializing libraries or event listeners
    
    // Cleanup function if needed
    return () => {
      // Cleanup code if necessary
    };
  }, [isNavVisible]); // Dependencies, if any

  return (
    <>
      <section id="body-pd" className={`body ${isNavVisible ? 'body-pd' : ''} bg-light`}>
        <header className={`header ${isNavVisible ? 'body-pd' : ''}`} id="header">
          <div className="header_toggle" onClick={handleNavToggle}>
            <i className={`bx ${isNavVisible ? 'bx-x' : 'bx-menu'}`} id="header-toggle"></i>
          </div>
          <div className="header_img">
            <img src="https://i.imgur.com/hczKIze.jpg" alt="Profile" />
          </div>
        </header>
        <div className={`l-navbar ${isNavVisible ? 'show' : ''}`} id="nav-bar">
          <nav className="nav">
            <div>
              <a href="#" className="nav_logo a">
                <i className="bx bx-layer nav_logo-icon"></i>
                <span className="nav_logo-name">Acedemic Affa</span>
              </a>
              <div className="nav_list">
                {['Dashboard', 'Users', 'Messages', 'Bookmark', 'Files', 'Stats'].map((name) => (
                  <a
                    href="#"
                    key={name}
                    className={`nav_link ${activeLink === name ? 'active' : ''} a`}
                    onClick={() => handleLinkClick(name)}
                  >
                    <i className={`bx ${name === 'Dashboard' ? 'bx-grid-alt' : 'bx-user'} nav_icon`}></i>
                    <span className="nav_name">{name}</span>
                  </a>
                ))}
              </div>
            </div>
            <a href="#" className="nav_link">
              <i className="bx bx-log-out nav_icon"></i>
              <span className="nav_name">SignOut</span>
            </a>
          </nav>
        </div>
        {/* Container Main start */}
        <div className="height-100 pt-4 bg-light">
          <h4>Main Components</h4>
          
        </div>
      </section>
    </>
  );
};

export default StudentDashboard;
