import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/Login.css";

function Login() {
  // Variables
  const [studentID, setStudentID] = useState("");
  const [password, setPassword] = useState("");

  const handleStudentID = (event) => {
    setStudentID(event.target.value);
  };

  const handlePassword = (event) => {
    setPassword(event.target.value);
  };
  const loginProcess = () => {
    alert(studentID + " : " + password);
  };

  return (
    <>
      <div class="maindiv">
        {/* animated Area */}
        <div class="area">
          <ul class="circles">
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
          </ul>
        </div>

        <div class="container m-5" id="container">
          <div class="form-container sign-in">
            <>
              <h1>Sign In</h1>
              <span class="mb-5"> use your student id & password</span>
              <input
                type="text"
                placeholder="Student ID"
                value={studentID}
                onChange={handleStudentID}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={handlePassword}
              />
              <button onSubmit={loginProcess}>Sign In</button>
            </>
          </div>
          <div class="toggle-container d-none d-md-block">
            <div class="toggle">
              <div class="toggle-panel toggle-left">
                <h1>Welcome Back!</h1>
                <p>Enter your personal details to use all of site features</p>
                <button class="hidden" id="login">
                  Sign In
                </button>
              </div>
              <div class="toggle-panel toggle-right">
                <h2>ACADEMIC DIVISION </h2>
                <h5 class="mb-4">LEARNING MANAGEMENT SYSTEM</h5>
                <p>
                  Register with your personal Student Id And Password to Join
                  Classes And Access Any Other features
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
