import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/Login.css";

function Login() {
  // Variables
  const [studentID, setStudentID] = useState("");
  const [password, setPassword] = useState("");

  const handleStudentID = (event) => {
    setStudentID(event.target.value);
  };

  const handlePassword = (event) => {
    setPassword(event.target.value);
  };

  const loginProcess = (event) => {
    window.location = "student";
  };



  return (
    <>
      <div className="maindiv">
        {/* animated Area */}
        <div className="area">
          <ul className="circles">
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
            <li></li>
          </ul>
        </div>

        <div className="container m-5" id="container">
          <div className="form-container half-width-md sign-in">
            <div className="form">
              <h3 className="fw-semibold p-3 loginCardColor rounded-4 text-white d-md-none text-center">ACADEMIC DIVISION</h3>
              <h4 className="mt-3">Sign In</h4>
              <span className="mb-5"> use your user id & password</span>
              <input
                type="text"
                placeholder="User ID"
                className="w-100"
                value={studentID}
                onChange={handleStudentID}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={handlePassword}
              />
              <button onClick={loginProcess}>Sign In</button>
            </div>
          </div>
          <div className="toggle-container d-none d-md-block">
            <div className="toggle">
              <div className="toggle-panel toggle-left">
                <h1>Welcome Back!</h1>
                <p>Enter your personal details to use all of site features</p>

              </div>
              <div className="toggle-panel toggle-right">
                <h2>ACADEMIC DIVISION </h2>
                <h5 className="mb-4">LEARNING MANAGEMENT SYSTEM</h5>
                <p>
                  Register with your personal User Id And Password to Access 
                  Clasess And Any Other features
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
