import React, { useState } from "react";
import { DateRangePicker } from "react-date-range";
import "react-date-range/dist/styles.css"; // main css file
import "react-date-range/dist/theme/default.css"; // theme css file

const ManageClass = () => {
  const [state, setState] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);
  const [showPicker, setShowPicker] = useState(false);
  const [dateRange, setDateRange] = useState("");

  const handleSelect = (ranges) => {
    const { startDate, endDate } = ranges.selection;
    setState([ranges.selection]);
    setDateRange(`${startDate.toLocaleDateString()} - ${endDate.toLocaleDateString()}`);
    setShowPicker(false); // Close the picker after selection
  };

  return (
    <>
      <div className="col-12">
        <div className="row">
          <div className="col-12 mt-4">
            <h2 className="fw-bold">Manage Classes</h2>
          </div>
          <div className="col-12 mt-4">
            <button onClick={() => setShowPicker(!showPicker)} className="btn btn-primary">
              {showPicker ? "Close Picker" : "Select Date Range"}
            </button>
            {showPicker && (
              <DateRangePicker
                ranges={state}
                onChange={handleSelect}
              />
            )}
            <input
              type="text"
              value={dateRange}
              readOnly
              className="form-control mt-2"
              placeholder="Selected Date Range"
            />
          </div>
        </div>
      </div>
    </>
  );
};

export default ManageClass;
