import { useState } from "react";
import { DateRangePicker } from "rsuite";
import ReactDOM from 'react-dom';
import 'rsuite/dist/rsuite.min.css';

const ManageClass = () => {
  const [dateRange, setDateRange] = useState(null);

  const handleDateChange = (value) => {
    setDateRange(value);
  };

  const formatDate = (date) => {
    return date.toLocaleDateString();
  };

  const renderDateRange = () => {
    if (!dateRange) {
      return <p>No date range selected</p>;
    }

    const [startDate, endDate] = dateRange;

    if (startDate.toLocaleDateString() === endDate.toLocaleDateString()) {
      return <p>Selected Date: {formatDate(startDate)}</p>;
    } else {
      return <p>Selected Range: {formatDate(startDate)} - {formatDate(endDate)}</p>;
    }
  };
  return (
    <>
      <div className="col-12">
        <div className="row">
          <h2 className="fw-bold mt-4">Manage Classes</h2>
          <div className="col-12">
            <>
              <DateRangePicker onChange={handleDateChange} />
              <div>
                {dateRange ? (
                  <p>
                    Selected Range: {dateRange[0].toLocaleDateString()} -{" "}
                    {dateRange[1].toLocaleDateString()}
                  </p>
                ) : (
                  <p>No date range selected</p>
                )}
              </div>
            </>
          </div>
        </div>
      </div>
    </>
  );
};

export default ManageClass;
