import React, { useState } from "react";
import { DateRangePicker } from "react-date-range";
import "react-date-range/dist/styles.css"; // main css file
import "react-date-range/dist/theme/default.css"; // theme css file

const ManageClass = () => {
  const [state, setState] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);

  const handleSelect = (ranges) => {
    setState([ranges.selection]);
    console.log(ranges.selection); // Log the selected range
  };

  return (
    <>
      <div className="col-12">
        <div className="row">
          <div className="col-12 mt-4">
            <h2 className="fw-bold">Manage Classes</h2>
          </div>
          <div class="row">
    <div class="col-sm-6 mb-3 mb-sm-0">
      <div data-coreui-locale="en-US" data-coreui-toggle="date-range-picker"></div>
    </div>
    <div class="col-sm-6">
      <div
        data-coreui-start-date="2022/08/03"
        data-coreui-end-date="2022/08/17"
        data-coreui-locale="en-US"
        data-coreui-toggle="date-range-picker">
      </div>
    </div>
  </div>
        </div>
      </div>
    </>
  );
};

export default ManageClass;
